<main class="dshbd innerContainer">

	<div class="dshbd_profile">
		<img src="/imgs/profile image.svg" alt="profile">
		<div class="dshbd_follow">
			<h3>Jimmy's Kitchen</h3>
			<p>100 Followers</p>
		</div>

		<div class="dshbd_time">
			<h3>HOURS</h3>
			<p>Mon - Fri</p>
			<p>8:00 a.m. - 8:00 p.m.</p>
		</div>

		<div class="dshbd_controller">
		<!-- switch showing -->
			<button class="btn btn_activated"><h3>KITCHEN OPENED</h3></button>
			<button class="btn btn_warning"><h3>KITCHEN CLOSED</h3></button>
		</div>

		<div class="dshbd_orderFilter">
			<p>Orders</p>
			<button class="btn btn_activated">All</button>
			<button class="btn btn_deactivated">Waiting</button>
			<button class="btn btn_deactivated">Finished</button>
			
		</div>
	</div>

	<div class="dshbd_review">
		<div class="dshbd_review_summary">
			<h3>Hi, Jimmy. It's 13:00, Wed, May 30th. </h3>
			<p>You have <span class="span_warning"> 2 </span> dishes waiting,</p>
			<p><span class="span_normal">28</span> quota left.</p>
			<p>You have made <span class="span_normal">15</span> dishes today.</p>
		</div>

		<div class="dshbd_review_waiting">
			<h3 class="afterline warning">WAITING</h3>
			<div class="order">
				<div class="order_user">
					<div class="order_basicInfo clearfix">
						<div class="floatLeft">
							<h2 class="order_name_number">Coral <p>Order No. 999999</p></h2>
							<h3 class="order_tel">647-888-9999</h3>
						</div>
						<p class="order_timeStamp floatRight">Ordered on 11:30am today.</p>
					</div>
					<div class="order_deliver_status afterline clearfix">
						<div class="order_deliverInfo">
							<p class="order_deliver">Deliver/Pick-up</p>
							<h3 class="order_add">999 QUEEN ST. W, M5V 1A1 </h3>
						</div>
						<div class="order_status">
							<p class="warning">Haven't deliver yet.</p>
							<p class="normal hide">Waiting for pick-up.</p>
							<p class="normal hide">Finished.</p>
							<button class="btn btn_weak">cancel the change</button>
							<button class="btn btn_activated">SEND</button>
							<button class="btn btn_fade hide">FINISH</button>
							<button class="btn btn_sub hide">FINISHED</button>

						</div>
					</div>
				</div>

				<div class="order_dishes afterline">
					<div class="order_item ">

						<div class="order_names_number">

							<h3 class="floatLeft">1</h3>

							<div class="order_names">
								<h3 class="order_item_name">Best steak in the town</h3>
								<h3 class="order_item_altname">滋滋牛排</h3>
							</div>

						</div>

						<div class="order_requirement">
							<p>+Medium Rare</p>
							<p>+w/ smash potato </p>
						</div>

						<div class="order_item_price">
							<h3>$2</h3>
						</div>

					</div>

					<div class="order_item ">
						<div class="order_names_number">
							<h3 class="floatLeft">1</h3>					
							<div class="order_names">
								<h3 class="order_item_name">Best steak in the town</h3>
								<h3 class="order_item_altname">滋滋牛排</h3>
							</div>
						</div>
						<div class="order_requirement">
							<p>+Medium Rare</p>
							<p>+w/ smash potato </p>
						</div>
						<div class="order_item_price">
							<h3>$2</h3>
						</div>
					</div>

					<div class="order_item ">
						<div class="order_names_number">
							<h3 class="floatLeft">1</h3>			
							<div class="order_names">
								<h3 class="order_item_name">Best steak in the town</h3>
								<h3 class="order_item_altname">滋滋牛排</h3>
							</div>
						</div>
						<div class="order_requirement">
							<p>+Medium Rare</p>
							<p>+w/ smash potato </p>
						</div>
						<div class="order_item_price">
							<h3>$2</h3>
						</div>
					</div>
				<!-- end of the orders -->
				</div>

				<div class="order_total clearfix">
					<p class="order_total-item floatLeft">Total: <span>3</span> items </p>
					<p class="order_total_price floatRight">Total Price <span>$11($5 delivery fee)</span></p>
				</div>
				<button class="btn_sub btn">Print order</button>
			</div>
			<!-- end of the one person's order -->

			<div class="order">
